import React from "react";
import styles from './product.module.css';
import customFetch from '../../api';

const CardCart = ({ postre, isInCart=true, cartProducts }) => {



  const handleDelete = (id) => { 
          customFetch("DELETE", "cart" + id, {body: cartProducts})
          .then(res => {window.location.reload()}) 
       };     

       return(
          <div className={styles.product} >
               <h4>{postre.title}</h4>
               <p><b>Preço: {postre.price}</b>€</p>
               <img src={postre.thumbnailUrl} alt="tartas"/>
               <div className={styles.button}>
                    {isInCart && <button className={styles.addToCart} onClick={() => handleDelete(cartProducts._id)}>DELETE </button>}
               </div>

          </div>
     )
};

export default CardCart;
