import React from "react";
import styles from './productList.module.css';
import Product from "../productCard/productCard";
import { useState } from "react";
import { useEffect } from "react";
import { useModal } from "../../hooks/useModal";
import Modal from "../modal/modal";



const ProductList = ({ searchValue, isInCart=false }) => {
     const [selectedItem, setSelectedItem] = useState();
     const [filteredData, setFilteredData] = useState([]);
     const [isOpenModal, openModal, closeModal] = useModal(false);
     const [cartProducts, setCartProducts] = useState([]);


     useEffect(() => {
          if (searchValue) {
            const filtered = filteredData.filter(item => {
              return item.type === searchValue;
            });
           setFilteredData(filtered);
          }
        }, [searchValue, filteredData]);


        useEffect(() => {
          fetch('http://localhost:3001/addProduct')
            .then((response) => {
               if (!response.ok){
                  throw new Error("error")
               }
               return response.json();
            })
            .then((json) => {
             setFilteredData(json);
            })
            .catch((error) => {
               console.log(error);
            })
        }, [setFilteredData]);


     const addToCart = (item) => {       
          setCartProducts([...cartProducts, item]);
     }


     return(  
          <div className={styles.productList} >

               {filteredData.map( item => <Product addToCart={addToCart} item={item} id={item._id} key={item._id} 
               onClick={() => {
                    setSelectedItem(item); 
                    openModal()  
      }} />)}

          {selectedItem && 
               <Modal isOpen={isOpenModal} closeModal={closeModal}>
                    <h2> {selectedItem.title}</h2>
                    <h3><b>Preço: </b> {selectedItem.price}€</h3>
                    <p>{selectedItem.description}</p>
                    <img src={selectedItem.thumbnailUrl} width="400" alt="img"/> 
               </Modal>}

          </div>
     )
};

export default ProductList;
