import React from "react";
import styles from './cart.module.css'

import { useEffect, useState } from "react";
import Product from "../../components/productCard/productCard";


const Cart = ({addToCart}) => {

     const [onCart, setOnCart] = useState([]);

     useEffect(() => {
          fetch('http://localhost:3001/cart')
            .then(response => {
               if (!response.ok){
                  throw new Error("error")
               }
               return response.json();
            })
            .then((json) => {
               setOnCart(json);
            })
            .catch((error) => {
               console.log(error);
            });
        }, [setOnCart]);

        const calcTotalPrice = (list) => {
          let totalPrice = 0;
          list.forEach((item) => {
               totalPrice = totalPrice + item.price;
          });

          return totalPrice;

     }

     return(
          <div className={styles.cart}>
               <h3>My Cart</h3>
               <div className={styles.onCart}>
                    {onCart && onCart.length > 0 && onCart.map((item) => {
                         return(  
                              <Product addToCart={addToCart} item={item} id={item._id} key={item._id} />)
               })}

                    {(!onCart || onCart.length === 0) && (<p>0 productos</p>)}
                    <p>Total Price:{calcTotalPrice(onCart)} â‚¬ </p>
               </div>     

          </div>
     )
};

export default Cart;
