import React from "react";
import styles from './cart.module.css'
import CardCart from "../../components/CardCart/cardCart";
import { useEffect, useState } from "react";
import customFetch from '../../api';

const Cart = ({cartProducts, addToCart}) => {

     const [selectedItem, setSelectedItem] = useState();

     useEffect(() => {
          fetch('http://localhost:3001/cart')
            .then(response => {
               if (!response.ok){
                  throw new Error("error")
               }
               return response.json();
            })
            .then((json) => {
               addToCart(json);
            })
            .catch((error) => {
               console.log(error);
            });
        }, [addToCart]);

     const calcTotalPrice = (list) => {
          let totalPrice = 0;
          list.forEach((postre) => {
               totalPrice = totalPrice + postre.price;
          });
          return totalPrice;
     }


     return(
          <div>
               <h3>My Cart</h3>
               <div className={styles.cart}>
                    {cartProducts && cartProducts.length > 0 && cartProducts.map((postre) => {
                         return(  
                              <CardCart postre={postre} isInCart={true} id={postre._id} key={postre._id}  onClick={() => {
                          setSelectedItem(postre);

               }}/>
                         )
                    })}
               </div> 
                    {(!cartProducts || cartProducts.length === 0) && (<p>0 productos</p>)}
                    <p>Total Price:{calcTotalPrice(cartProducts)} â‚¬ </p>


          </div>
     )
};

export default Cart;
