          fetch('http://localhost:3001/addProducts')
            .then((response) => {
               if (!response.ok){
                  throw new Error("error")
               }
               return response.json();
            })
            .then((json) => {
             setData(json);
            })
            .catch((error) => {
               console.log(error);
            })
        }, []);
