import React from "react";
import styles from './product.module.css';
// import customFetch from '../../api';

const CardCart = ({ selectedItem }) => {

     const handleDelete = (id) => { 
          fetch('http://localhost:3010/cart/'+ id, {
             method: 'DELETE',
             headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(selectedItem)
          })
          .then(res => {window.location.reload()}) 
       }
     // const handleDelete = () => { 
     //      customFetch("DELETE", "cart/+id", {body: item})
     //      .then(res => {window.location.reload()}) 
     //   }; 
       console.log(selectedItem)
     return(
          <div className={styles.product} >
               <h4>{selectedItem.title}</h4>
               <p><b>Preço: {selectedItem.price}</b>€</p>
               <img src={selectedItem.thumbnailUrl} alt="tartas"/>
               <div className={styles.button}>
                    <button className={styles.addToCart} onClick={() => handleDelete(selectedItem._id)}>DELETE </button>
               </div>

          </div>
     )
};

export default CardCart;
