import React from "react";
import styles from './product.module.css';
import { useState, useEffect } from "react";
// import customFetch from '../../api';

const CardCart = ({ item }) => {

     const [cart, setCart] = useState();

     useEffect(() => {
          fetch('http://localhost:3001/cart')
            .then(response => {
               if (!response.ok){
                  throw new Error("error")
               }
               return response.json();
            })
            .then((json) => {
               setCart(json);
            })
            .catch((error) => {
               console.log(error);
            });
        }, [setCart]);


     const handleDelete = (id) => { 
          fetch('http://localhost:3010/cart/'+ id, {
             method: 'DELETE',
             headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(item)
          })
          .then(res => {window.location.reload()}) 
       }
     // const handleDelete = () => { 
     //      customFetch("DELETE", "cart/+id", {body: item})
     //      .then(res => {window.location.reload()}) 
     //   }; 
       console.log(item)
     return(
          <div className={styles.product} >
               <h4>{cart.title}</h4>
               <p><b>Preço:{cart.price}</b>€</p>
               <p>{cart.description}</p>
               <img src={cart.thumbnailUrl} alt="tartas"/>
               <div className={styles.button}>
                    <button className={styles.addToCart} onClick={() => handleDelete(cart._id)}>DELETE </button>
               </div>

          </div>
     )
};

export default CardCart;
