import React from "react";
import styles from './cart.module.css'
import CardCart from "../../components/CardCart/cardCart";
import { useEffect, useState } from "react";



const Cart = ({ cartProducts, setCartProducts }) => {



     useEffect(() => {
          fetch('http://localhost:3001/cart')
            .then(response => {
               if (!response.ok){
                  throw new Error("error")
               }
               return response.json();
            })
            .then((json) => {
               setCartProducts(json);
            })
            .catch((error) => {
               console.log(error);
            });
        }, [setCartProducts]);

     const calcTotalPrice = (list) => {
          let totalPrice = 0;
          list.forEach((item) => {
               totalPrice = totalPrice + item.price;
          });
          return totalPrice;
     }


     return(
          <div>
               <h3>My Cart</h3>
               <div className={styles.cart}>
                    {cartProducts && cartProducts.length > 0 && cartProducts.map((item) => {
                         return(  
                              <CardCart item={item} isInCart={true} id={item._id} key={item._id}  />)
               })}
               </div> 
                    {(!cartProducts || cartProducts.length === 0) && (<p>0 productos</p>)}
                    <p>Total Price:{calcTotalPrice(cartProducts)} â‚¬ </p>


          </div>
     )
};

export default Cart;
