import React from "react";
import styles from './product.module.css'
import customFetch from '../../api';
import Modal from "../modal/modal";
import { useModal } from "../../hooks/useModal";


const Product = ({ item, isInCart=false, onClick, addToCart, selectedItem }) => {

     const [isOpenModal, openModal, closeModal] = useModal(false);

     const onCart = () => {   
          const data ={
               type:item.type,
               price:item.price,
               description:item.description,
               title: item.title,
               format:item.format,
               thumbnailUrl:item.thumbnailUrl
          }
          customFetch("POST", "cart", {body: data})
          .then(() => {
               addToCart()
               openModal()
          })
          .catch(error => {
               console.error(error);
          })


     }
     return(
          <div className={styles.product} >
               <h4>{item.title}</h4>
               <p><b>Preço: {item.price}</b>€</p>
               <img src={item.thumbnailUrl} alt="tartas"/>
               <div className={styles.button}>
                    {!isInCart && <button className={styles.addToCart} 
                    onClick={() => onCart()}>Add to Cart +</button>}

                    <button className={styles.more} onClick={onClick}>Show more...</button>
               </div>

               {selectedItem && 
               <Modal isOpen={isOpenModal} closeModal={closeModal}>
                    <h2> {selectedItem.title}</h2>
                    <h3><b>Preço: </b> {selectedItem.price}€</h3>
                    <img src={selectedItem.thumbnailUrl} width="400" alt="img"/>
                    <h1>Tem sido agregado no carrinho!</h1>
               </Modal>
          }  

          </div>
     )
};

export default Product;
