import React from "react";
import styles from './productList.module.css';
import Product from "../productCard/productCard";
import { useState } from "react";
import { useEffect } from "react";
import { useModal } from "../../hooks/useModal";
import Modal from "../modal/modal";
import customFetch from '../../api';
import Cart from '../../pages/cart/cart'


const ProductList = ({ searchValue, isInCart=false }) => {
     const [selectedItem, setSelectedItem] = useState();
     const [filteredData, setFilteredData] = useState();
     const [isOpenModal, openModal, closeModal] = useModal(false);
     const [cartProducts, setCartProducts] = useState([]);
     const [data, setData] = useState([]);


  useEffect(() => {
     if (searchValue !== '') {
          setFilteredData(data.filter(({ type }) => {
            const regex = new RegExp(searchValue);
            return regex.test(type.toLocaleLowerCase());
          }))
        }
      }, [searchValue, setFilteredData, data]);

      useEffect(() => {
          if (data.length) {
            setFilteredData();
          }
        }, [data, setFilteredData]);

        useEffect(() => {
          fetch('http://localhost:3001/addProducts')
            .then((response) => {
               if (!response.ok){
                  throw new Error("error")
               }
               return response.json();
            })
            .then((json) => {
             setData(json);
            })
            .catch((error) => {
               console.log(error);
            })
        }, []);
        console.log(data)

     const addToCart = (postre) => {       
          setCartProducts([...cartProducts, postre]);
     }

     const onCart = () => {
          const data = { 
             type: selectedItem.type,
             id: selectedItem._id,
             price: selectedItem.price,
             description: selectedItem.description,
             title:selectedItem.title,
             thumbnailUrl: selectedItem.thumbnailUrl
            }
          customFetch("POST", "cart", {body: data})
          .then(() => {
               addToCart()
          })
          .catch(error => {
               console.error(error);
          })
     }


     return(  
          <div className={styles.productList} >

               {filteredData.map(postre => <Product addToCart={addToCart} postre={postre} id={postre._id} key={postre._id} onClick={() => {
               setSelectedItem(postre); 
               openModal()
               onCart();

      }} />)}

          {selectedItem && 
               <Modal isOpen={isOpenModal} closeModal={closeModal}>
                    <p> {selectedItem.title}</p>
                    <p><b>Preço: </b> {selectedItem.price}€</p>
                    <p>{selectedItem.description}</p>
                    <img src={selectedItem.thumbnailUrl} width="400" alt="img"/>
                    <button onClick={() => {onCart()}} >add to cart</button>

               </Modal>}
               <Cart cartProducts={cartProducts} addToCart={addToCart}/>
          </div>
     )
};

export default ProductList;
