import React from "react";
import styles from './product.module.css'
import customFetch from '../../api';

const Product = ({ item, isInCart=false, onClick, addToCart, cartProducts }) => {


     const onCart = () => {   
          const data ={
               type:cartProducts.type,
               price:cartProducts.price,
               description:cartProducts.description,
               title: cartProducts.title,
               format:cartProducts.format,
               thumbnailUrl:cartProducts.thumbnailUrl
          }
          customFetch("POST", "cart", {body: data})
          .then(() => {
               addToCart()
          })
          .catch(error => {
               console.error(error);
          })
     }
     return(
          <div className={styles.product} >
               <h4>{item.title}</h4>
               <p><b>Preço: {item.price}</b>€</p>
               <img src={item.thumbnailUrl} alt="tartas"/>
               <div className={styles.button}>
                    {!isInCart && <button className={styles.addToCart} onClick={() => onCart()}>Add to Cart +</button>}
                    <button className={styles.more} onClick={onClick}>Show more...</button>
               </div>

          </div>
     )
};

export default Product;
