import React from "react";
import styles from './product.module.css'

const Product = ({ item, isInCart=false, onClick }) => {

     const onCart = () => {
          const data = { 
             type: selectedItem.type,
             id: selectedItem._id,
             price: selectedItem.price,
             description: selectedItem.description,
             title:selectedItem.title,
             thumbnailUrl: selectedItem.thumbnailUrl
            }
          customFetch("POST", "cart", {body: data})
          .then(() => {
               addToCart()
          })
          .catch(error => {
               console.error(error);
          })
     }
     return(
          <div className={styles.product} >
               <h4>{item.title}</h4>
               <p><b>Preço: {item.price}</b>€</p>
               <img src={item.thumbnailUrl} alt="tartas"/>
               <div className={styles.button}>
                    {!isInCart && <button className={styles.addToCart} onClick={() => onCart()}>Add to Cart +</button>}
                    <button className={styles.more} onClick={onClick}>Show more...</button>
               </div>

          </div>
     )
};

export default Product;
