import React, { useEffect, useState } from 'react';
import {useForm} from 'react-hook-form';
import styles from '../addProduct/addProduct.module.css';
import customFetch from '../../api';
import CardCart from '../../components/CardCart/cardCart'
import ManageProducts from '../../components/manageProducts/manageProducts';
import Modal from '../../components/modal/modal';
import { useModal } from '../../hooks/useModal';


const EditProduct = () => {

   const {register, handleSubmit, formState:{ errors} } = useForm();
   const [product, setProduct] = useState([]);
   const [isOpenModal, openModal, closeModal] = useModal(false);
   const [selectedItem, setSelectedItem] = useState();


   const data = {

   }

   const onSubmit = data => {
      setProduct(data)
      customFetch("PATCH", "addProduct", {body: data})
   };
    const handleOnClick = () => {
      window.location.reload()
    }
    useEffect(() => {
      if (data.length) {
        setProduct(data);
      }
    }, [data]);

    useEffect(() => {
      fetch('http://localhost:3001/addProduct')
        .then((response) => {
           if (!response.ok){
              throw new Error("error")
           }
           return response.json();
        })
        .then((json) => {
         setProduct(json);
        })
        .catch((error) => {
           console.log(error);
        })
    }, []);

   return (
      <div className={styles.main}>
         <div className={styles.content}>
         <h2>Edit a Product</h2>
         </div>


         <div className={styles.wrapper}>
           <div>
               <ManageProducts />
            </div>

         {product.map(item => <CardCart item={item} id={item._id} key={item._id}   
        onClick={() => {
            setSelectedItem(item);
            openModal();           
      }} />)} 

        {selectedItem && 
            <Modal isOpen={isOpenModal} closeModal={closeModal}>
            <img src={selectedItem.thumbnailUrl} width="300" alt="img"/>
            <div className={styles.buttons}>
            <form onSubmit={handleSubmit(onSubmit)}>
               <label>Type</label>
               <input type="text"  {...register("type", { required: true})} />
               {errors.type?.type === 'required' && <p className={styles.error}>This field is required</p>}
               {errors.type?.type === 'pattern' && <p className={styles.error}>Incorrect name</p>}
               <br/>
               <label>Title</label>
               <input type="text"  {...register("title", { required: true })} />
               {errors.title && <p className={styles.error}>This field is required</p>}
               <br/>
               <label>Description</label>
               <textarea  {...register("description", { required: true })} >
               {errors.description && <p className={styles.error}>This field is required</p>}
               </textarea>
               <br/>
               <label>Price</label>
               <input type="number"  {...register("price", { required: true })} />
               {errors.price && <p className={styles.error}>This field is required</p>}
               <br/>
               <label>ThumbnailUrl</label>
               <input type="text"  {...register("thumbnailUrl")} />


               <div className={styles.buttons}>
                  <input className={styles.send} type="submit"  />
                  <button onClick={() => {handleOnClick()}} className={styles.reload}>reset</button>
               </div>    
           </form>
            </div>
            </Modal>}

         </div>

        </div>
   );
};

export default EditProduct;
